def decide_cluster_order(node, childnode2clust):
    """ranks the clusters from 0 to n (with 0 being the best) depending on the properties/similarities of the child nodes contained in each cluster
    Args:
        node ([type]): [description]
        childnode2clust ([type]): [description]

    Returns:
        [type]: [description]
    """
    # if len(clust2childnodes.keys())==1:
    #     return childnode2clust
    if ( (node.type == "mod_seq_charge")):
        return childnode2clust
    else:
        clust2childnodes = aqutils.invert_dictionary(childnode2clust)
        return order_by_property(node, clust2childnodes, "cv", reverse=True)


import alphaquant.classify_ions as aqclass

def order_by_property(node, clust2childnodes, property = 'min_intensity', reverse = False):
    clust2score = {}
    for clust in clust2childnodes.keys():
        summed_val = 0
        for node in clust2childnodes.get(clust):
            clust_property = node.__dict__[property] #equals to e.g. node.min_intensity, but allows to give the property as string
            if "intensity" in property:
                clust_property = 2**clust_property
            summed_val+=clust_property
        if "cv" in property:
            summed_val = summed_val/len(clust2childnodes.get(clust))
        if reverse:
            summed_val = 1/summed_val
        clust2score[clust] = summed_val
    childnode2clust = get_node2clust_from_scored(clust2score, clust2childnodes)
    return childnode2clust



def predict_and_order_by_ml_score(node,clust2childnodes, ml_classifier, featurenames_ml):
    clust2score = {}
    for clust in clust2childnodes.keys():
        parameter_df = aqclass.collect_node_parameters(clust2childnodes.get(clust), w_annot = False)
        parameter_df = parameter_df.reindex(featurenames_ml, axis=1).replace(np.nan, -1)
        X = parameter_df.to_numpy()
        ml_score = max([x[1] for x in ml_classifier.predict_proba(X)])
        clust2score[clust] = ml_score
    node.ml_score = max(clust2score.values())
    childnode2clust = get_node2clust_from_scored(clust2score, clust2childnodes)
    return childnode2clust




def propagate_and_order_by_ml_score(node, clust2childnodes):
    clust2score = {}
    for clust in clust2childnodes.keys():
        ml_score = max([x.ml_score for x in clust2childnodes.get(clust)])
        clust2score[clust] = ml_score
    node.ml_score = max(clust2score.values())
    childnode2clust = get_node2clust_from_scored(clust2score, clust2childnodes)
    return childnode2clust

def get_node2clust_from_scored(clust2score, clust2nodes):
    node2clust = {}
    clusts = list(clust2score.keys())
    clusts.sort(key = lambda x : clust2score.get(x), reverse= True)
    for clust in clusts:
        for node in clust2nodes.get(clust):
            node2clust[node] =clust
    return node2clust

