import numpy as np
def get_fcs_ions(diffions):
    fcs = np.ones(len(diffions))
    for idx in range(len(diffions)):
        fc_ions = np.nanmedian([ion.fc for ion in diffions[idx]])
        fcs[idx] = fc_ions
    return fcs

